        "type": "component",
        "name": "header.tsx",
        "code": "\"use client\";\nimport React from \"react\";\nimport { createPortal } from \"react-dom\";\nimport { WordmarkIcon } from \"@/components/logo\";\nimport { MenuToggleIcon } from \"@/components/menu-toggle-icon\";\nimport { Button, buttonVariants } from \"@/components/ui/button\";\nimport { useScroll } from \"@/hooks/use-scroll\";\nimport { cn } from \"@/lib/utils\";\n\nexport function Header() {\n  const [open, setOpen] = React.useState(false);\n  const scrolled = useScroll(10);\n\n  const links = [\n    {\n      label: \"Features\",\n      href: \"#\",\n    },\n    {\n      label: \"Pricing\",\n      href: \"#\",\n    },\n    {\n      label: \"About\",\n      href: \"#\",\n    },\n  ];\n\n  React.useEffect(() => {\n    if (open) {\n      document.body.style.overflow = \"hidden\";\n    } else {\n      document.body.style.overflow = \"\";\n    }\n    return () => {\n      document.body.style.overflow = \"\";\n    };\n  }, [open]);\n\n  return (\n    <header\n      className={cn(\"sticky top-0 z-50 w-full border-transparent border-b\", {\n        \"border-border bg-background/95 backdrop-blur-lg supports-[backdrop-filter]:bg-background/50\":\n          scrolled,\n      })}\n    >\n      <nav className=\"mx-auto flex h-14 w-full max-w-5xl items-center justify-between px-4\">\n        <div className=\"rounded-md p-2 hover:bg-accent\">\n          <WordmarkIcon className=\"h-4\" />\n        </div>\n        <div className=\"hidden items-center gap-2 md:flex\">\n          {links.map((link, i) => (\n            <a\n              className={buttonVariants({ variant: \"ghost\" })}\n              href={link.href}\n              key={i}\n            >\n              {link.label}\n            </a>\n          ))}\n          <Button variant=\"outline\">Sign In</Button>\n          <Button>Get Started</Button>\n        </div>\n        <Button\n          aria-controls=\"mobile-menu\"\n          aria-expanded={open}\n          aria-label=\"Toggle menu\"\n          className=\"md:hidden\"\n          onClick={() => setOpen(!open)}\n          size=\"icon\"\n          variant=\"outline\"\n        >\n          <MenuToggleIcon className=\"size-5\" duration={300} open={open} />\n        </Button>\n      </nav>\n      <MobileMenu className=\"flex flex-col justify-between gap-2\" open={open}>\n        <div className=\"grid gap-y-2\">\n          {links.map((link) => (\n            <a\n              className={buttonVariants({\n                variant: \"ghost\",\n                className: \"justify-start\",\n              })}\n              href={link.href}\n              key={link.label}\n            >\n              {link.label}\n            </a>\n          ))}\n        </div>\n        <div className=\"flex flex-col gap-2\">\n          <Button className=\"w-full bg-transparent\" variant=\"outline\">\n            Sign In\n          </Button>\n          <Button className=\"w-full\">Get Started</Button>\n        </div>\n      </MobileMenu>\n    </header>\n  );\n}\n\ntype MobileMenuProps = React.ComponentProps<\"div\"> & {\n  open: boolean;\n};\n\nfunction MobileMenu({ open, children, className, ...props }: MobileMenuProps) {\n  if (!open || typeof window === \"undefined\") {\n    return null;\n  }\n\n  return createPortal(\n    <div\n      className={cn(\n        \"bg-background/95 backdrop-blur-lg supports-[backdrop-filter]:bg-background/50\",\n        \"fixed top-14 right-0 bottom-0 left-0 z-40 flex flex-col overflow-hidden border-y md:hidden\"\n      )}\n      id=\"mobile-menu\"\n    >\n      <div\n        className={cn(\n          \"data-[slot=open]:zoom-in-97 ease-out data-[slot=open]:animate-in\",\n          \"size-full p-4\",\n          className\n        )}\n        data-slot={open ? \"open\" : \"closed\"}\n        {...props}\n      >\n        {children}\n      </div>\n    </div>,\n    document.body\n  );\n}",
